import React, { useState } from 'react';
import { getBuyersByCriteria, getSellerOutput, getProductSales, getBuyersOfProduct, getMostActiveBuyers } from '../../services/api';

const QueryForm = () => {
    const [queryType, setQueryType] = useState('');
    const [criteria, setCriteria] = useState({});
    const [results, setResults] = useState([]);

    const handleChange = (e) => {
        setCriteria({ ...criteria, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let response;
        switch (queryType) {
            case 'buyers_by_criteria':
                response = await getBuyersByCriteria(criteria);
                break;
            case 'seller_output':
                response = await getSellerOutput(criteria);
                break;
            case 'product_sales':
                response = await getProductSales(criteria);
                break;
            case 'buyers_of_product':
                response = await getBuyersOfProduct(criteria);
                break;
            case 'most_active_buyers':
                response = await getMostActiveBuyers(criteria);
                break;
            default:
                break;
        }
        setResults(response.data);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <select onChange={(e) => setQueryType(e.target.value)} value={queryType}>
                    <option value="">Select Query</option>
                    <option value="buyers_by_criteria">Buyers By Criteria</option>
                    <option value="seller_output">Seller Output</option>
                    <option value="product_sales">Product Sales</option>
                    <option value="buyers_of_product">Buyers Of Product</option>
                    <option value="most_active_buyers">Most Active Buyers</option>
                </select>
                {queryType === 'buyers_by_criteria' && (
                    <>
                        <input type="text" name="product_id" onChange={handleChange} placeholder="Product ID" />
                        <input type="text" name="min_volume" onChange={handleChange} placeholder="Min Volume" />
                        <input type="date" name="start_date" onChange={handleChange} />
                        <input type="date" name="end_date" onChange={handleChange} />
                    </>
                )}
                {/* Add input fields for other query types similarly */}
                <button type="submit">Run Query</button>
            </form>
            <div>
                <h2>Results</h2>
                <pre>{JSON.stringify(results, null, 2)}</pre>
            </div>
        </div>
    );
};

export default QueryForm;
