import React, { useState } from 'react';
import { Button, Table, Form } from 'react-bootstrap';
import { getSuppliersByCriteria } from '../../services/api';

const SuppliersByCriteria = () => {
    const [criteria, setCriteria] = useState({
        product_id: '',
        min_volume: '',
        start_date: '',
        end_date: ''
    });
    const [suppliers, setSuppliers] = useState([]);
    const [totalSuppliers, setTotalSuppliers] = useState(0);

    const handleChange = (e) => {
        setCriteria({ ...criteria, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await getSuppliersByCriteria(criteria);
            setSuppliers(response.data.suppliers);
            setTotalSuppliers(response.data.total_suppliers);
        } catch (error) {
            console.error('Failed to fetch suppliers', error);
        }
    };

    return (
        <div className="container">
            <h3>Suppliers By Criteria</h3>
            <Form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Product ID:</label>
                    <input
                        type="text"
                        name="product_id"
                        value={criteria.product_id}
                        onChange={handleChange}
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label>Minimum Volume:</label>
                    <input
                        type="number"
                        name="min_volume"
                        value={criteria.min_volume}
                        onChange={handleChange}
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label>Start Date:</label>
                    <input
                        type="date"
                        name="start_date"
                        value={criteria.start_date}
                        onChange={handleChange}
                        className="form-control"
                    />
                </div>
                <div className="form-group">
                    <label>End Date:</label>
                    <input
                        type="date"
                        name="end_date"
                        value={criteria.end_date}
                        onChange={handleChange}
                        className="form-control"
                    />
                </div>
                <Button type="submit" className="btn btn-dark mt-2">Search</Button>
            </Form>
            <h4 className="mt-4">Total Suppliers: {totalSuppliers}</h4>
            {suppliers.length > 0 && (
                <Table striped bordered hover variant="dark" className="mt-2">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                    </tr>
                    </thead>
                    <tbody>
                    {suppliers.map(supplier => (
                        <tr key={supplier.id}>
                            <td>{supplier.id}</td>
                            <td>{supplier.name}</td>
                        </tr>
                    ))}
                    </tbody>
                </Table>
            )}
        </div>
    );
};

export default SuppliersByCriteria;
