import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:5000/api',
});

// Sales API
export const getSales = () => api.get('/sales');
export const getSaleById = (id) => api.get(`/sales/${id}`);
export const createSale = (sale) => api.post('/sales', sale);
export const updateSale = (id, sale) => api.put(`/sales/${id}`, sale);
export const deleteSale = (id) => api.delete(`/sales/${id}`);

// Query API
export const getBuyersByCriteria = (criteria) => api.get('/buyers_by_criteria', { params: criteria });
export const getSellerOutput = (criteria) => api.get('/seller_output', { params: criteria });
export const getProductSales = (criteria) => api.get('/product_sales', { params: criteria });
export const getBuyersOfProduct = (criteria) => api.get('/buyers_of_product', { params: criteria });
export const getMostActiveBuyers = (criteria) => api.get('/most_active_buyers', { params: criteria });

// Human API
export const getHumans = () => api.get('/humans');
export const getHumanById = (id) => api.get(`/humans/${id}`);
export const createHuman = (human) => api.post('/humans', human);
export const updateHuman = (id, human) => api.put(`/humans/${id}`, human);
export const deleteHuman = (id) => api.delete(`/humans/${id}`);

// Product API
export const getProducts = () => api.get('/products');

// Outlet API
export const getOutlets = () => api.get('/outlets');

// Staff API
export const getStaff = () => api.get('/staff');

// Items Stock Outlets API
export const getItems = () => api.get('/staff');

export default api;
