import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import { createSale, updateSale, getSales } from '../../services/api';

const AddSale = () => {
    const [form, setForm] = useState({
        human_id: '',
        product_id: '',
        outlet_id: '',
        date: '',
        price: '',
        quantity: '',
        staff_id: '',
    });

    const navigate = useNavigate();
    const { id } = useParams();
    const isEditing = Boolean(id);

    useEffect(() => {
        if (isEditing) {
            fetchSale();
        }
    }, [id]);

    const fetchSale = async () => {
        try {
            const response = await getSales(id);
            setForm(response.data);
        } catch (error) {
            console.error('Failed to fetch sale', error);
        }
    };

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await updateSale(id, form);
            } else {
                await createSale(form);
            }
            navigate('/sales');
        } catch (error) {
            console.error('Failed to save sale', error);
        }
    };

    return (
        <div className="container">
            <h3>{isEditing ? 'Edit Sale' : 'Add New Sale'}</h3>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Human ID:</label>
                    <input
                        type="number"
                        name="human_id"
                        value={form.human_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Product ID:</label>
                    <input
                        type="number"
                        name="product_id"
                        value={form.product_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Outlet ID:</label>
                    <input
                        type="number"
                        name="outlet_id"
                        value={form.outlet_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Date:</label>
                    <input
                        type="date"
                        name="date"
                        value={form.date}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Price:</label>
                    <input
                        type="number"
                        name="price"
                        value={form.price}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Quantity:</label>
                    <input
                        type="number"
                        name="quantity"
                        value={form.quantity}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Staff ID:</label>
                    <input
                        type="number"
                        name="staff_id"
                        value={form.staff_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <button type="submit" className="btn btn-dark">Save</button>
                <Link to="/sales" className="btn btn-dark" style={{ marginLeft: '10px' }}>Back</Link>
            </form>
        </div>
    );
};

export default AddSale;
