import React, { useState, useEffect } from 'react';
import { Button, Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { getStaff, deleteStaff } from '../../services/api';

export default function StaffPage() {
    const [staff, setStaff] = useState([]);
    const [showTable, setShowTable] = useState(true);

    useEffect(() => {
        fetchStaff();
    }, []);

    const fetchStaff = async () => {
        try {
            const response = await getStaff();
            setStaff(response.data);
        } catch (error) {
            console.error('Failed to fetch staff', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteStaff(id);
            fetchStaff();
        } catch (error) {
            console.error('Failed to delete staff member', error);
        }
    };

    return (
        <div>
            <h2>Staff</h2>
            <Button onClick={() => setShowTable(!showTable)} className="btn btn-dark mb-2">
                {showTable ? 'Hide Table' : 'Show Table'}
            </Button>
            {showTable && (
                <div className="table-container">
                    <Link to="/staff/add" className="btn btn-dark mb-2">Add Staff Member</Link>
                    <Table striped bordered hover variant="dark">
                        <thead>
                        <tr>
                            <th>ID</th>
                            <th>Job Type</th>
                            <th>Human</th>
                            <th>Outlet</th>
                            <th>Salary</th>
                            <th>Actions</th>
                        </tr>
                        </thead>
                        <tbody>
                        {staff.map(member => (
                            <tr key={member.id}>
                                <td>{member.id}</td>
                                <td>{member.job_type_id}</td>
                                <td>{member.human_id}</td>
                                <td>{member.outlet_id}</td>
                                <td>{member.salary}</td>
                                <td>
                                    <Link to={`/staff/edit/${member.id}`} className="btn btn-danger">Edit</Link>
                                    <Button onClick={() => handleDelete(member.id)} className="btn btn-danger" style={{ marginLeft: 10 }}>Delete</Button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </Table>
                </div>
            )}
        </div>
    );
}
