import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import { createSale, updateSale, getSales, getHumans, getProducts, getOutlets, getStaff, getHumanById } from '../../services/api';

const AddSale = () => {
    const [form, setForm] = useState({
        human_id: '',
        product_id: '',
        outlet_id: '',
        date: '',
        price: '',
        quantity: '',
        staff_id: '',
    });

    const [humans, setHumans] = useState([]);
    const [products, setProducts] = useState([]);
    const [outlets, setOutlets] = useState([]);
    const [staff, setStaff] = useState([]);
    const [staffNames, setStaffNames] = useState({});

    const navigate = useNavigate();
    const { id } = useParams();
    const isEditing = Boolean(id);

    useEffect(() => {
        fetchLists();
        if (isEditing) {
            fetchSale();
        }
    }, [id]);

    const fetchLists = async () => {
        try {
            const [humansResponse, productsResponse, outletsResponse, staffResponse] = await Promise.all([
                getHumans(),
                getProducts(),
                getOutlets(),
                getStaff()
            ]);
            setHumans(humansResponse.data);
            setProducts(productsResponse.data);
            setOutlets(outletsResponse.data);
            setStaff(staffResponse.data);

            // Fetch human names for staff members
            const staffNamesTemp = {};
            await Promise.all(staffResponse.data.map(async staffMember => {
                const humanResponse = await getHumanById(staffMember.human_id);
                staffNamesTemp[staffMember.id] = humanResponse.data.name;
            }));
            setStaffNames(staffNamesTemp);
        } catch (error) {
            console.error('Failed to fetch lists', error);
        }
    };

    const fetchSale = async () => {
        try {
            const response = await getSales(id);
            setForm(response.data);
        } catch (error) {
            console.error('Failed to fetch sale', error);
        }
    };

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await updateSale(id, form);
            } else {
                await createSale(form);
            }
            navigate('/sales');
        } catch (error) {
            console.error('Failed to save sale', error);
        }
    };

    return (
        <div className="container">
            <h3>{isEditing ? 'Edit Sale' : 'Add New Sale'}</h3>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Human:</label>
                    <select
                        name="human_id"
                        value={form.human_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    >
                        <option value="">Select Human</option>
                        {humans.map(human => (
                            <option key={human.id} value={human.id}>
                                {human.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Product:</label>
                    <select
                        name="product_id"
                        value={form.product_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    >
                        <option value="">Select Product</option>
                        {products.map(product => (
                            <option key={product.id} value={product.id}>
                                {product.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Outlet:</label>
                    <select
                        name="outlet_id"
                        value={form.outlet_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    >
                        <option value="">Select Outlet</option>
                        {outlets.map(outlet => (
                            <option key={outlet.id} value={outlet.id}>
                                {outlet.name}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label>Date:</label>
                    <input
                        type="date"
                        name="date"
                        value={form.date}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Price:</label>
                    <input
                        type="number"
                        name="price"
                        value={form.price}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Quantity:</label>
                    <input
                        type="number"
                        name="quantity"
                        value={form.quantity}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Staff:</label>
                    <select
                        name="staff_id"
                        value={form.staff_id}
                        onChange={handleChange}
                        className="form-control"
                        required
                    >
                        <option value="">Select Staff</option>
                        {staff.map(staffMember => (
                            <option key={staffMember.id} value={staffMember.id}>
                                {staffNames[staffMember.id]}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-dark">Save</button>
                <Link to="/sales" className="btn btn-dark" style={{ marginLeft: '10px' }}>Back</Link>
            </form>
        </div>
    );
};

export default AddSale;
