import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const ManageUsers = () => {
    const [email, setEmail] = useState('');
    const [role, setRole] = useState('');
    const [message, setMessage] = useState('');

    const handleRoleUpdate = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(`${API_URL}/role`, { email, role }, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setMessage(response.data.msg);
        } catch (error) {
            setMessage('Error updating role');
            console.error(error);
        }
    };

    const handleUserDelete = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            const response = await axios.delete(`${API_URL}/user`, {
                headers: {
                    Authorization: `Bearer ${token}`
                },
                data: { email }
            });
            setMessage(response.data.msg);
        } catch (error) {
            setMessage('Error deleting user');
            console.error(error);
        }
    };

    return (
        <div>
            <h2>Manage Users</h2>
            <form onSubmit={handleRoleUpdate}>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="User Email" required />
                <input type="text" value={role} onChange={(e) => setRole(e.target.value)} placeholder="Role (admin, cashier, manager, owner)" required />
                <button type="submit">Update Role</button>
            </form>
            <form onSubmit={handleUserDelete}>
                <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="User Email" required />
                <button type="submit">Delete User</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default ManageUsers;
