import React from 'react';
import { Button, Table } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { getSales, deleteSale } from '../../services/api';

export default function SalesPage() {
    const [sales, setSales] = React.useState([]);
    const [currentPage, setCurrentPage] = React.useState(1);
    const [totalPages, setTotalPages] = React.useState(0);
    const [totalElements, setTotalElements] = React.useState(0);

    const recordPerPage = 5;

    const navigate = useNavigate();

    React.useEffect(() => {
        init(1);
    }, []);

    const init = (currentPage) => {
        getSales()
            .then(response => {
                // Assuming response.data contains the data directly
                setSales(response.data);
                setTotalPages(Math.ceil(response.data.length / recordPerPage));
                setTotalElements(response.data.length);
                setCurrentPage(currentPage);
            })
            .catch(error => {
                console.error(error);
            });
    };

    const handleDelete = (id) => {
        deleteSale(id)
            .then(() => {
                init(currentPage);
            })
            .catch(error => {
                console.log('Something went wrong', error);
            });
    };

    const showNextPage = () => {
        if (currentPage < Math.ceil(totalElements / recordPerPage)) {
            init(currentPage + 1);
        }
    };

    const showLastPage = () => {
        if (currentPage < Math.ceil(totalElements / recordPerPage)) {
            init(Math.ceil(totalElements / recordPerPage));
        }
    };

    const showFirstPage = () => {
        if (currentPage > 1) {
            init(1);
        }
    };

    const showPrevPage = () => {
        if (currentPage > 1) {
            init(currentPage - 1);
        }
    };

    return (
        <div>
            <h2>Sales</h2>
            <div className="table-container">
                <Link to="/sales/add" className="btn btn-dark mb-2">Add Sale</Link>
                <Table striped bordered hover variant="dark">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>Human ID</th>
                        <th>Product ID</th>
                        <th>Outlet ID</th>
                        <th>Date</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {sales && sales.map(sale => (
                        <tr key={sale.id}>
                            <td>{sale.id}</td>
                            <td>{sale.human_id}</td>
                            <td>{sale.product_id}</td>
                            <td>{sale.outlet_id}</td>
                            <td>{sale.date}</td>
                            <td>{sale.price}</td>
                            <td>{sale.quantity}</td>
                            <td>
                                <Link to={`/sales/edit/${sale.id}`} className="btn btn-danger">Edit</Link>
                                <Button onClick={() => handleDelete(sale.id)} className="btn btn-danger" style={{ marginLeft: 10 }}>Delete</Button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </Table>
                <div>
                    <div style={{ float: 'left', color: '#D10000' }}>
                        Page {currentPage} of {totalPages}
                    </div>
                    <div style={{ float: 'right' }}>
                        <nav>
                            <ul className="pagination">
                                <li className="page-item">
                                    <a type="button" className="page-link" disabled={currentPage === 1} onClick={showFirstPage}>First</a>
                                </li>
                                <li className="page-item">
                                    <a type="button" className="page-link" disabled={currentPage === 1} onClick={showPrevPage}>Previous</a>
                                </li>
                                <li className="page-item">
                                    <a type="button" className="page-link" disabled={currentPage === totalPages} onClick={showNextPage}>Next</a>
                                </li>
                                <li className="page-item">
                                    <a type="button" className="page-link" disabled={currentPage === totalPages} onClick={showLastPage}>Last</a>
                                </li>
                            </ul>
                        </nav>
                    </div>
                </div>
                <Link to="/" className="btn btn-dark mb-2">Back</Link>
            </div>
        </div>
    );
}
